import { useState } from "react";
import { useInventoryStore } from "../store/inventoryStore";
import Table from "../components/Table";
import ExportButtons from "../components/ExportButtons";

export default function Products() {
  const { products, addProduct, updateProduct, removeProduct } = useInventoryStore();
  const [form, setForm] = useState({ id: "", name: "", sku: "", price: 0, stock: 0, minStock: 0 });

  const submit = (e) => {
    e.preventDefault();
    if (!form.id) {
      const id = crypto.randomUUID();
      addProduct({ ...form, id, price: Number(form.price), stock: Number(form.stock), minStock: Number(form.minStock) });
    } else {
      updateProduct(form.id, { ...form, price: Number(form.price), stock: Number(form.stock), minStock: Number(form.minStock) });
    }
    setForm({ id: "", name: "", sku: "", price: 0, stock: 0, minStock: 0 });
  };

  const edit = (p) => setForm(p);
  const del = (id) => removeProduct(id);

  const columns = [
    { key: "name", label: "Nome" },
    { key: "sku", label: "SKU" },
    { key: "price", label: "PreÃ§o" },
    { key: "stock", label: "Estoque" },
    { key: "minStock", label: "MÃ­nimo" },
  ];

  return (
    <div className="grid gap-4 md:grid-cols-3">
      <div className="card md:col-span-1">
        <h2 className="font-semibold mb-3">{form.id ? "Editar produto" : "Novo produto"}</h2>
        <form onSubmit={submit} className="space-y-3">
          <div>
            <label className="label">Nome</label>
            <input className="input" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} required />
          </div>
          <div>
            <label className="label">SKU</label>
            <input className="input" value={form.sku} onChange={e=>setForm({...form, sku:e.target.value})} required />
          </div>
          <div className="grid grid-cols-3 gap-2">
            <div>
              <label className="label">PreÃ§o</label>
              <input type="number" step="0.01" className="input" value={form.price} onChange={e=>setForm({...form, price:e.target.value})} />
            </div>
            <div>
              <label className="label">Estoque</label>
              <input type="number" className="input" value={form.stock} onChange={e=>setForm({...form, stock:e.target.value})} />
            </div>
            <div>
              <label className="label">MÃ­nimo</label>
              <input type="number" className="input" value={form.minStock} onChange={e=>setForm({...form, minStock:e.target.value})} />
            </div>
          </div>
          <div className="flex gap-2">
            <button className="btn btn-primary" type="submit">{form.id ? "Salvar" : "Adicionar"}</button>
            {form.id && <button onClick={()=>setForm({ id: "", name: "", sku: "", price: 0, stock: 0, minStock: 0 })} type="button" className="btn btn-danger">Cancelar</button>}
          </div>
        </form>
      </div>
      <div className="md:col-span-2 space-y-3">
        <div className="flex items-center justify-between">
          <h2 className="font-semibold">Lista de produtos</h2>
          <ExportButtons filename="produtos" data={products} />
        </div>
        <Table columns={columns} data={products} />
        <div className="text-sm text-slate-600">
          <p className="mt-2">Clique em um item para editar ou excluir.</p>
          <ul className="mt-2">
            {products.map(p => (
              <li key={p.id} className="flex justify-between border-b py-1">
                <span className="cursor-pointer" onClick={()=>edit(p)}>{p.name} â {p.sku}</span>
                <button className="text-rose-600" onClick={()=>del(p.id)}>Excluir</button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
