import { Routes, Route, NavLink } from "react-router-dom";
import Dashboard from "./pages/Dashboard";
import Products from "./pages/Products";
import Inventory from "./pages/Inventory";
import Movements from "./pages/Movements";
import PurchaseOrders from "./pages/PurchaseOrders";
import Sales from "./pages/Sales";
import CommissionReport from "./pages/reports/CommissionReport";

export default function App() {
  const navLinkClass = ({ isActive }) =>
    `px-3 py-2 rounded-lg ${isActive ? "bg-sky-600 text-white" : "text-slate-700 hover:bg-slate-100"}`;

  return (
    <div className="min-h-screen">
      <header className="bg-white border-b shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-3 flex items-center gap-4">
          <h1 className="text-xl font-semibold">ðŸ“¦ Controle de Estoque</h1>
          <nav className="flex items-center gap-2 text-sm">
            <NavLink to="/" className={navLinkClass} end>Dashboard</NavLink>
            <NavLink to="/produtos" className={navLinkClass}>Produtos</NavLink>
            <NavLink to="/estoque" className={navLinkClass}>Estoque</NavLink>
            <NavLink to="/movimentos" className={navLinkClass}>Movimentos</NavLink>
            <NavLink to="/compras" className={navLinkClass}>Compras</NavLink>
            <NavLink to="/vendas" className={navLinkClass}>Vendas</NavLink>
            <NavLink to="/relatorio-comissao" className={navLinkClass}>Rel. ComissÃ£o</NavLink>
          </nav>
        </div>
      </header>
      <main className="max-w-7xl mx-auto px-4 py-6">
        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/produtos" element={<Products />} />
          <Route path="/estoque" element={<Inventory />} />
          <Route path="/movimentos" element={<Movements />} />
          <Route path="/compras" element={<PurchaseOrders />} />
          <Route path="/vendas" element={<Sales />} />
          <Route path="/relatorio-comissao" element={<CommissionReport />} />
        </Routes>
      </main>
    </div>
  );
}
