import * as XLSX from "xlsx";
import Papa from "papaparse";

export default function ExportButtons({ filename = "dados", data = [] }) {
  const onExportExcel = () => {
    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Planilha");
    XLSX.writeFile(wb, `${filename}.xlsx`);
  };

  const onExportCSV = () => {
    const csv = Papa.unparse(data);
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${filename}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="flex gap-2">
      <button onClick={onExportExcel} className="btn btn-success">Exportar Excel</button>
      <button onClick={onExportCSV} className="btn btn-primary">Exportar CSV</button>
    </div>
  );
}
